import styled from "styled-components";
import { } from "antd";

export const Header = styled('div')`
  .menu-desktop{
    height: 124px;
    box-shadow: 0 1px 5px 0px rgba(0,0,0,0.2);
  }
`;

export const Wrapper = styled('div')`
  width: 100%;
`;

export const Nav = styled('div')`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: space-between;
  padding: 0.5rem 1rem;
  position: relative;
  width: 100%;
  top: 0;
`;

export const NavHeader = styled('div')`
    width: 100%;
    background: #3c4451;
    border: 0;
`;

export const UlCustom = styled('ul')`
  display: flex;
  margin: 0;
  justify-content: flex-end;
  flex-wrap: wrap;
  padding-left: 0;
  list-style: none;
  align-items: center;
`;

export const SelectIcon = styled('div')`
  display: flex;
  width: 20%;
  justify-content: flex-end;
  a{
    color: black;
    text-decoration: none;
    svg{
      font-size: 24px;
    }
    &:hover{
      cursor: pointer;
      color: #717FE0;
    }
    position: relative;
    span{
      color: #fff;
      font-weight: 500;
      padding: 1px 2px;
      line-height: 1;
      font-size: 12px;
      position: absolute;
      background-color: #717fe0;
      top: -7px;
      right: -7px;
    }
  }
`;

export const Icon = styled('div')`
  svg{
    font-size: 22px;
